<?phprequire_once('Wool/App/Articles/Blog.php');class BlogPostsWidget extends Widget {	public static function define() {		return array(			"id" => "blogposts",			"controller" => "blog",			"name" => "Blog Posts",			"views" => array("default"),			"params" => array(				"number" => array(					"name" => "Show # Posts",					"default" => "5"				)			)		);	}		public function action() {		$this->controller->addHelper("blog");		$this->articles = Blog::summaries($this->param("number", 5))->rowSet();	}		public function configure() {			}}