<?phpclass ProductCollectionWidget extends Widget {	public static function define() {		return array(			"id" => "productcollection",			"controller" => "product",			"name" => "Product Collection",			"views" => array("default"),			"params" => array(				"category" => array(					"name" => "Category",					"default" => "1"				),				"something" => array(					"name" => "Something",					"default" => "Yo"				)			)		);	}		public function action() {		$this->products = new RowSet(<<<SQLselect p.*from product pjoin product_in_category pic on pic.productId = p.productIdwhere pic.categoryId = ?SQL		, $this->param("category", 0));	}		public function configure() {			}}