<?phprequire_once(dirname(__FILE__) . '/Boot.php');require_once('Wool/Framework/include.php');require_once('Wool/Framework/Db.php');require_once('Wool/Router.php');class Application {	private static $router;		public static function dispatch() {		Session::start();				self::$router = new Router;		self::$router->dispatch();	}		public static function revRoute($options) {		return self::$router->revRoute($options);	}		public static function errorLog() {		return Boot::errorLog();	}		public static function errorFatal($mask=null) {		return Boot::errorFatal($mask);	}}WoolDb::connect($GLOBALS['DB_HOST'], $GLOBALS['DB_NAME'], $GLOBALS['DB_USERNAME'], $GLOBALS['DB_PASSWORD']);