<?phprequire_once('Wool/Framework/Db/Schema.php');class TableSchema {	private $table;		public function __construct($table) {		$this->table = $table;	}		public function column($column) {		return new ColumnSchema($this->table, $column);	}		public function __call($fn, $args) {		array_unshift($args, $this->table);		return call_user_func_array(array("Schema", $fn), $args);	}}class ColumnSchema {	private $table;	private $column;		public function __construct($table, $column) {		$this->table = $table;		$this->column = $column;	}		public function __call($fn, $args) {		array_unshift($args, $this->table, $this->column);		return call_user_func_array(array("Schema", $fn), $args);	}}