<div class="pad">	<ol class="breadcrumbs">		<li><?php echo linkTo("Home", array("action"=>"index")) ?></li>		<li><?php echo linkTo(WoolTable::displayName($table), array("action"=>"table", "table"=>$table)) ?></li>		<li><?php $u = WoolTable::uniqueColumn($table); echo linkTo("Edit " . WoolTable::displayName($table) . " #" . $item->$u, array("action"=>"edit", "table"=>$table, "id"=>$item->$u)) ?></li>		<li>History</li>	</ol></div><div class="pad">	<div class="pod">		<table class="dataGrid" data-gridTable="<?php echo $table ?>" data-headerUpdate="<?php echo routeUri(array("controller"=>"api", "action"=>"headerUpdate")) ?>" data-dragRows="<?php echo routeUri(array("controller"=>"api", "action"=>"rowOrder")) ?>">			<thead>				<tr>					<th></th>					<?php foreach ($columns as $column) { ?>					<th class="dragable <?php echo WoolTable::columnEditable($table, $column) ? "editable" : "" ?>" data-column="<?php echo $column ?>"><?php echo WoolTable::columnName($table, $column) ?></th>					<?php } ?>					<th width="1"></th>				</tr>			</thead>						<tbody>				<?php if (!count($data)) { ?>				<tr>					<td colspan="999">No items found.</td>				</tr>				<?php } ?>				<?php $self->renderPartials("row", $data, "item", array("table"=>$table, "columns"=>$columns)) ?>			</tbody>		</table>				<div class="foot">			<div class="pod podNested pad">				<?php $self->renderPartial('/grid/nav', array("pager"=>$data)) ?>			</div>		</div>	</div></div>