<div class="pad">	<ol class="breadcrumbs">		<li><?php echo linkTo("Home", array("action"=>"index")) ?></li>		<li><?php echo linkTo(Schema::displayName($table), array("action"=>"table", "table"=>$table)) ?></li>		<li><?php $u = Schema::uniqueColumn($table); echo linkTo("Edit " . Schema::displayName($table) . " #" . $item->$u, array("action"=>"edit", "table"=>$table, "id"=>$item->$u)) ?></li>		<li>History</li>	</ol></div><div class="pad">	<div class="pod">		<table class="dataGrid">			<thead>				<tr>					<?php foreach ($columns as $column) { ?>					<th><?php echo Schema::columnName($table, $column) ?></th>					<?php } ?>					<th width="1"></th>				</tr>			</thead>						<tbody>				<?php if (!count($data)) { ?>				<tr>					<td colspan="999">No items found.</td>				</tr>				<?php } ?>				<?php $self->renderPartials("history_row", $data, "item", array("grid"=>$data, "table"=>$table, "columns"=>$columns)) ?>			</tbody>		</table>				<div class="foot">			<div class="pod podNested pad">				<?php $self->renderPartial('/grid/nav', array("pager"=>$data)) ?>			</div>		</div>	</div></div>