<table class="stdTbl">	<tr>		<th>Name</th>		<th>Message</th>		<th>Date</th>	</tr>		<?php foreach ($messages as $message) { ?>	<tr>		<td><?php echo $message->name ?></td>		<td><?php echo linkTo(truncate($message->message, 70), array("controller"=>"forum", "action"=>"message", "id"=>$message->id)) ?></td>		<td><?php echo $message->createdOn ?></td>	</tr>	<?php } ?></table>